package validator

import (
	"fmt"
	"reflect"
	"runtime"
	"testing"
)

type log struct{}

func (l *log) Info(msg string, args ...interface{}) {
	fmt.Printf(msg+"\n", args...)
}

func (l *log) Error(msg string, args ...interface{}) {
	fmt.Printf(msg+"\n", args...)
}

func setup(t *testing.T) *V {
	t.Helper()

	// copy of internal/testenv.MustHaveExternalNetwork()
	// because use of internal/ packages is prohibited,
	// but Validator tests require external network
	if runtime.GOOS == "js" {
		t.Skipf("skipping test: no external network on %s", runtime.GOOS)
	}
	if testing.Short() {
		t.Skipf("skipping test: no external network in -short mode")
	}

	spamlist := []string{"*@spam.host", "spam@email.host", "spam@*"}
	enforce := Enforce{
		Email:  true,
		Domain: true,
		MX:     true,
		SMTP:   false,
	}

	return New(spamlist, enforce, "test@ilydeen.org", &log{})
}

func TestNew(t *testing.T) {
	v := setup(t)

	if reflect.TypeOf(&V{}) != reflect.TypeOf(v) {
		t.Fail()
	}
}

func TestA(t *testing.T) {
	v := setup(t)
	tests := map[string]bool{
		"":              false,
		"example.com":   true,
		"doesnt.exists": false,
	}

	for host, expected := range tests {
		t.Run(host, func(t *testing.T) {
			result := v.A(host)

			if expected != result {
				t.Error(expected, "!=", result)
			}
		})
	}
}

