func TestEmailSPF(t *testing.T) {
	tests := []struct {
		email    string
		ip       net.IP
		expected bool
	}{
		{"iana@iana.org", net.IPv4(199, 91, 192, 1), true},
	}
	for _, test := range tests {
		t.Run(test.email, func(t *testing.T) {
			v := setup(t)
			v.cfg.Email.SPF = true
			v.cfg.Email.MX = false
			v.cfg.Email.SMTP = false

			result := v.Email(test.email, test.ip)

			if test.expected != result {
				t.Error("spf check of", test.email, "failed")
			}
		})
	}
}

func TestGetBase(t *testing.T) {
	tests := map[string]string{
		"example.com":             "example.com",
		"sub.example.com":         "example.com",
		"sub.sub.sub.example.com": "example.com",
		"example.etke.host":       "example.etke.host",
		"sub.example.etke.host":   "example.etke.host",
		"example.co.uk":           "example.co.uk",
		"sub.example.co.uk":       "example.co.uk",
		"example.gov.la":          "example.gov.la",
		"sub.example.gov.la":      "example.gov.la",
		"example.com.sg":          "example.com.sg",
		"sub.example.com.sg":      "example.com.sg",
	}
	for host, expected := range tests {
		t.Run(host, func(t *testing.T) {
			v := setup(t)
			v.cfg.Domain.PrivateSuffixes = []string{".etke.host"}

			result := v.GetBase(host)

			if expected != result {
				t.Error(expected, "!=", result)
			}
		})
	}
}
